CREATE OR REPLACE PROCEDURE baca_gerar_funcao_net ( nm_usuario_p text) AS $body$
DECLARE


cd_funcao_w			integer;
nr_seq_modulo_w			bigint;
ie_tipo_cliente_w		varchar(1);

C01 CURSOR FOR
	SELECT	cd_funcao,
		nr_seq_modulo
	from	funcao;


BEGIN

open C01;
loop
fetch C01 into
	cd_funcao_w,
	nr_seq_modulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	/* Funções da OPS */

	if (nr_seq_modulo_w  (721, 1012, 1013, 1014, 1015, 1016, 1017, 1018, 1019, 1020, 1032)) then
		ie_tipo_cliente_w	:= 'O';
	/* Funções utilizadas também pela OPS */

	elsif (cd_funcao_w in (4, 6, 25, 40, 135, 140, 137, 291, 801, 813, 815,
				830, 851, 854, 855, 859, 860, 923, 937, 1111)) then
		ie_tipo_cliente_w	:= 'A';
	/* Funções do prestador */

	else
		ie_tipo_cliente_w	:= 'P';
	end if;

	insert into funcao_net(nr_sequencia, cd_funcao, ie_tipo_cliente,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
		nm_usuario_nrec)
	values (	nextval('funcao_net_seq'), cd_funcao_w, ie_tipo_cliente_w,
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
