SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_gerar_hist_prev_migr () AS $body$
DECLARE


nr_seq_gerencia_w	bigint;
dt_previsao_w		timestamp;
nr_seq_hist_prev_w	bigint;

c01 CURSOR FOR
SELECT	distinct(nr_seq_gerencia)
from	w_previsao_proj_desenv;

c02 CURSOR FOR
SELECT	distinct(trunc(dt_atualizacao_nrec,'dd'))
from	w_previsao_proj_desenv
where	nr_seq_gerencia = nr_seq_gerencia_w;


BEGIN
open c01;
loop
fetch c01 into nr_seq_gerencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	open c02;
	loop
	fetch c02 into dt_previsao_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		select	nextval('w_hist_prev_proj_desenv_seq')
		into STRICT	nr_seq_hist_prev_w
		;

		insert into w_hist_prev_proj_desenv(
			nr_sequencia,
			nm_usuario_previsao,
			dt_previsao,
			nr_seq_gerencia,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nm_usuario,
			dt_atualizacao)
		values (
			nr_seq_hist_prev_w,
			'Tasy',
			dt_previsao_w,
			nr_seq_gerencia_w,
			'Rafael',
			clock_timestamp(),
			'Rafael',
			clock_timestamp());

		update	w_previsao_proj_desenv
		set	nr_seq_hist_prev = nr_seq_hist_prev_w
		where	nr_seq_gerencia = nr_seq_gerencia_w
		and	trunc(dt_atualizacao_nrec,'dd') = dt_previsao_w;
		end;
	end loop;
	close c02;
	end;
end loop;
close c01;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
