SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_gerar_horarios_adep () AS $body$
DECLARE

 
nr_prescricao_w		bigint;

c01 CURSOR FOR 
SELECT	nr_prescricao 
from	prescr_medica a 
where	dt_prescricao > clock_timestamp() - interval '20 days' 
and	exists (SELECT	1 
		from 	prescr_material x 
		where	x.nr_prescricao = a.nr_prescricao 
		and	x.ie_suspenso	<> 'S') 
and	not exists (	select	1 
			from	prescr_mat_hor x 
			where	x.nr_prescricao = a.nr_prescricao) 
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	CALL gerar_prescr_mat_hor(nr_prescricao_w, null,1, 'Tasy','N','N',null);
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_gerar_horarios_adep () FROM PUBLIC;

