DECLARE


nm_objeto_w	varchar(50);
nr_seq_obj_w	bigint;
var_count_w	bigint := 0;

c01 CURSOR FOR
SELECT	nm_objeto
from	objeto_sistema
where	coalesce(nr_sequencia::text, '') = '';


BEGIN
open c01;
loop
fetch c01 into nm_objeto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	var_count_w	:= var_count_w + 1;

	select	nextval('objeto_sistema_seq')
	into STRICT	nr_seq_obj_w
	;

	update	objeto_sistema
	set	nr_sequencia	= nr_seq_obj_w
	where	nm_objeto	= nm_objeto_w;

	if (var_count_w = 1000) then
		begin
		var_count_w	:= 0;
		commit;
		end;
	end if;
	end;
end loop;
close c01;

commit;

end;
