SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_gerar_sus_longa_perm ( nr_seq_protocolo_p bigint) AS $body$
DECLARE

 
 
nr_interno_conta_w		bigint;
nr_atendimento_w		bigint;
nr_seq_apac_w		bigint;
nr_seq_protocolo_w	bigint;
dt_competencia_w		timestamp;
qt_apac_w		integer;
ds_detalhe_w		varchar(255);
ie_gera_longa_perm_unif_w 	varchar(1);
qt_aih_unif_w		integer;
cd_estabelecimento_w	smallint;

/* obter dados do detalhe */
 
c01 CURSOR FOR 
	SELECT	b.nr_interno_conta, 
		b.nr_atendimento 
	from	conta_paciente b 
	where	b.nr_seq_protocolo	= nr_seq_protocolo_p;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_interno_conta_w, 
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	select	count(*) 
	into STRICT	qt_aih_unif_w 
	from	sus_aih_unif 
	where	nr_atendimento	= nr_atendimento_w;
 
	select	coalesce(cd_estabelecimento,1) 
	into STRICT	cd_estabelecimento_w 
	from	atendimento_paciente 
	where	nr_atendimento	= nr_atendimento_w;
	 
	if (qt_aih_unif_w > 0) then 
		select	coalesce(max(ie_gera_longa_perm),'N') 
		into STRICT	ie_gera_longa_perm_unif_w 
		from	sus_parametros_aih 
		where	cd_estabelecimento	= cd_estabelecimento_w;
 
		if (ie_gera_longa_perm_unif_w	= 'S') then 
			CALL Sus_Gerar_Longa_Permanencia(nr_atendimento_w, nr_interno_conta_w, 'Philips');
		end if;
	end if;
	end;
end loop;
close c01;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
