SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_gestao_ir_ie_situacao () AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
ie_situacao_w		varchar(1);

C01 CURSOR FOR
	SELECT	cd_pessoa_fisica
	from	hd_pac_renal_cronico
	where	coalesce(ie_situacao::text, '') = ''
	order by 1;

BEGIN

open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	hd_pac_renal_cronico
	set	ie_situacao		= HD_Obter_Se_Paciente_Ativo(cd_pessoa_fisica_w)
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
