SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_glicemia_suspensa_adep () AS $body$
DECLARE


nr_seq_glicemia_w	bigint;
nr_prescricao_w	bigint;
nr_seq_proced_w	integer;

c01 CURSOR FOR
SELECT	nr_sequencia,
	nr_prescricao,
	nr_seq_procedimento
from	atend_glicemia
where	(nr_prescricao IS NOT NULL AND nr_prescricao::text <> '')
and	(nr_seq_procedimento IS NOT NULL AND nr_seq_procedimento::text <> '')
and	ie_status_glic <> 'S'
and	coalesce(substr(obter_se_proc_suspenso(nr_prescricao,nr_seq_procedimento),1,1),'N') = 'S'
order by
	nr_prescricao,
	nr_seq_procedimento;


BEGIN

open c01;
loop
fetch c01 into	nr_seq_glicemia_w,
			nr_prescricao_w,
			nr_seq_proced_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	update	atend_glicemia
	set	ie_status_glic	= 'S',
		nm_usuario		= 'BacaGlicADEP'
	where	nr_sequencia		= nr_seq_glicemia_w;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
