SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_gpi_estrutura () AS $body$
DECLARE


ie_tipo_w		varchar(1);
qt_dependentes_w	bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	gpi_estrutura a
where	coalesce(a.ie_tipo,'X') = 'X';

vet01	C01%RowType;


BEGIN

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_dependentes_w
	from	gpi_estrutura
	where	nr_seq_superior	= vet01.nr_sequencia;

	ie_tipo_w	:= 'A';

	if (qt_dependentes_w > 0) then
		ie_tipo_w	:= 'T';
	end if;

	update	gpi_estrutura
	set	ie_tipo	= ie_tipo_w
	where	nr_sequencia	= vet01.nr_sequencia;

	end;

end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
