SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_gpi_projeto_hist () AS $body$
DECLARE


ds_texto_w		text;
qt_registro_w		bigint;

C01 CURSOR FOR
SELECT	nr_sequencia,
	ds_historico
from	gpi_projeto_hist
where	(ds_historico IS NOT NULL AND ds_historico::text <> '');

vet01	C01%RowType;


BEGIN

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_texto_w	:= vet01.ds_historico;

	update	gpi_projeto_hist
	set	ds_texto	= ds_texto_w
	where	nr_sequencia	= vet01.nr_sequencia;

	end;
end loop;
close C01;

commit;

select	count(*)
into STRICT	qt_registro_w
from	gpi_projeto_hist
where	coalesce(ds_texto::text, '') = '';

if (qt_registro_w = 0) then

	select	count(*)
	into STRICT	qt_registro_w
	from	user_tab_columns
	where	table_name	= 'GPI_PROJETO_HIST'
	and	column_name	= 'DS_TEXTO'
	and	nullable	= 'Y';

	if (qt_registro_w > 0) then
		CALL exec_sql_dinamico('OS420459','alter table gpi_projeto_hist modify ds_texto not null');

		select	count(*)
		into STRICT	qt_registro_w
		from	user_tab_columns
		where	table_name	= 'GPI_PROJETO_HIST'
		and	column_name	= 'DS_HISTORICO'
		and	nullable	= 'Y';

		if (qt_registro_w = 0) then
			CALL exec_sql_dinamico('OS420459','alter table gpi_projeto_hist modify ds_historico null');
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
