CREATE OR REPLACE PROCEDURE baca_grupo_des_os_rev_migr () AS $body$
DECLARE


nr_seq_os_w	bigint;
nr_seq_grupo_w	bigint;

c01 CURSOR FOR
SELECT	m.nr_sequencia,
	p.nr_seq_grupo_des
from	man_ordem_servico m,
	proj_ordem_servico o,
	proj_projeto p
where	o.nr_seq_proj = p.nr_sequencia
and	p.nr_seq_gerencia = 9
and	p.nr_seq_classif = 14
and	o.ie_tipo_ordem = 'R'
and	o.nr_seq_ordem = m.nr_sequencia
and	p.nr_seq_grupo_des <> m.nr_seq_grupo_des
and	m.nr_seq_grupo_des in (50,19,65)
order by
	m.nr_sequencia;


BEGIN
open c01;
loop
fetch c01 into	nr_seq_os_w,
		nr_seq_grupo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	update	man_ordem_servico
	set	nr_seq_grupo_des = nr_seq_grupo_w,
		nm_usuario = 'B-G-D-O-R-M',
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_os_w;
	end;
end loop;
close c01;
commit;
end;
$body$
