CREATE OR REPLACE PROCEDURE baca_honorario_ipsemg ( nr_atendimento_P bigint, nr_seq_protocolo_p bigint) AS $body$
DECLARE

 
nr_seq_pacote_w		bigint		:= 0;
nr_interno_conta_w	bigint;

nr_sequencia_w		bigint;

C01 CURSOR FOR 
	SELECT	a.nr_seq_procedimento, 
			c.nr_interno_conta 
	from	atendimento_pacote a, 
		conta_paciente c 
	where a.nr_atendimento	= c.nr_atendimento 
	 and to_char(c.cd_convenio_calculo) = 38 
	 and to_char(a.cd_convenio) = 38 
	 and c.nr_atendimento = coalesce(nr_atendimento_p, c.nr_atendimento) 
	 and c.nr_seq_protocolo = nr_seq_protocolo_p 
	order by c.nr_atendimento;

C02 CURSOR FOR 
	SELECT nr_sequencia 
	from procedimento_paciente a 
	where nr_seq_proc_pacote = nr_seq_pacote_w 
	 and exists (SELECT 1 from procedimento_participante x 
			 where x.nr_sequencia = a.nr_sequencia);


BEGIN 
 
open C01;
loop 
	fetch C01 into	nr_seq_pacote_w, 
				nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
 
	open C02;
	loop 
		fetch C02 into nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
 
		update procedimento_participante 
		set vl_conta = vl_participante 
		where nr_sequencia = nr_sequencia_w;
 
	end loop;
	close C02;
 
	CALL Atualizar_Resumo_Conta(nr_interno_conta_w, 2);
end loop;
close C01;
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
