SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_horas_real_cron_proj_tmed () AS $body$
DECLARE


nr_seq_cronograma_w	bigint;
nr_seq_atividade_w	bigint;

c01 CURSOR FOR
SELECT	c.nr_sequencia,
	e.nr_sequencia
from  	proj_cron_etapa e,
	proj_cronograma c,
	proj_projeto p
where   e.nr_seq_cronograma = c.nr_sequencia
and     c.nr_seq_proj = p.nr_sequencia
and	c.nr_sequencia = 1992
and     p.nr_sequencia = 1644
group by
	c.nr_sequencia,
	e.nr_sequencia
order by
	c.nr_sequencia,
	e.nr_sequencia;


BEGIN
open c01;
loop
fetch c01 into	nr_seq_cronograma_w,
		nr_seq_atividade_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	update	proj_cron_etapa
	set	qt_hora_real = qt_hora_prev
	where	nr_sequencia = nr_seq_atividade_w;

	CALL atualizar_horas_etapa_cron(nr_seq_atividade_w);
	CALL atualizar_total_horas_cron(nr_seq_cronograma_w);
	end;
end loop;
close c01;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_horas_real_cron_proj_tmed () FROM PUBLIC;

