CREATE OR REPLACE PROCEDURE baca_ie_permite_visita () AS $body$
DECLARE

 
nr_atendimento_w			bigint		:= 0;
nr_registros_w			bigint;

C000 CURSOR FOR 
SELECT 
	nr_atendimento 
from 	atendimento_Paciente 
where coalesce(ie_permite_visita::text, '') = '';


BEGIN 
 
nr_registros_w	:= 0;
OPEN C000;
LOOP 
FETCH C000 INTO nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on c000 */
	begin 
	update atendimento_paciente a 
	set ie_permite_visita	= 'S' 
	where nr_atendimento = nr_atendimento_w;
	nr_registros_w := nr_registros_w + 1;
	/*if (nr_registros_w /1000) = trunc(nr_registros_w / 1000) then 
		begin 
		insert into logxxxx_tasy values(sysdate, 'Tasy', 999, nr_registros_w); 
		commit; 
		end;	 
	end if;*/
 
	exception 
		when others then 
			nr_registros_w	:= nr_registros_w;
	end;
END LOOP;
CLOSE C000;
/*insert into logxxxx_tasy values(	sysdate, 'Tasy', 999, nr_registros_w);*/
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
