CREATE OR REPLACE PROCEDURE baca_importa_evol_tasymed ( cd_pessoa_fisica_p text, cd_profissional_p text, nm_usuario_export_p text, nr_atendimento_p bigint default null, nr_seq_pepa_p bigint default null, nr_seq_cliente_p bigint default null) AS $body$
DECLARE

	
c01 CURSOR FOR
	SELECT	a.cd_medico,
			a.cd_pessoa_fisica, 
			b.dt_atualizacao, 
			b.nm_usuario, 
			b.dt_evolucao, 
			'' ds_evolucao, 
			b.qt_peso,
			b.qt_superf_corporal,
			b.nr_sequencia
	from med_cliente a, med_evolucao b 
	where a.nr_sequencia = b.nr_seq_cliente
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.cd_medico = cd_profissional_p
	and  a.nr_sequencia = nr_seq_cliente_p
	and (coalesce(nr_atendimento_p::text, '') = '' or b.nr_atendimento =  nr_atendimento_p or coalesce(b.nr_atendimento::text, '') = '')
	and coalesce(b.ie_exportado,'N') = 'N'
	order by dt_atualizacao;
	
c01_w c01%rowtype;

nr_sequencia_w evolucao_paciente.cd_evolucao%type;

ie_tipo_evolucao_w usuario.ie_tipo_evolucao%type;


BEGIN

if (coalesce(nr_atendimento_p::text, '') = '' or (nr_seq_pepa_p IS NOT NULL AND nr_seq_pepa_p::text <> '')) then

open C01;
loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			
			begin
			
				select 	nextval('evolucao_paciente_seq')
				into STRICT	nr_sequencia_w
				;
			
				select	coalesce(max(ie_tipo_evolucao),1)
				into STRICT	ie_tipo_evolucao_w
				from	usuario 
				where	nm_usuario = c01_w.nm_usuario;	
		
				insert into evolucao_paciente(
					cd_evolucao,
					dt_evolucao,
					ie_evolucao_clinica,
					cd_pessoa_fisica,
					dt_atualizacao,
					nm_usuario,
					cd_medico,
					qt_peso,
					qt_superf_corporia,
					ie_nivel_atencao,
					dt_liberacao,
					ie_situacao,
					ie_tipo_evolucao,
					dt_atualizacao_nrec,
					nr_seq_atend_cons_pepa
					) values (
					nr_sequencia_w,
					c01_w.dt_evolucao,
					'E',
					c01_w.cd_pessoa_fisica,
					c01_w.dt_atualizacao,
					c01_w.nm_usuario,
					c01_w.cd_medico,
					c01_w.qt_peso,
					c01_w.qt_superf_corporal,
					'S',
					c01_w.dt_atualizacao,
					'A',
					ie_tipo_evolucao_w,
					c01_w.dt_atualizacao,
					nr_seq_pepa_p);
					
					CALL copia_campo_long_de_para_novo(
						'MED_EVOLUCAO',
						'DS_EVOLUCAO',
						' where nr_sequencia = :nr_sequencia ',
						'nr_sequencia='||c01_w.nr_sequencia,
						'EVOLUCAO_PACIENTE',
						'DS_EVOLUCAO',
						' where cd_evolucao = :cd_evolucao ',
						'cd_evolucao='||nr_sequencia_w,
						'L');
						
					update med_evolucao
						set ie_exportado = 'S',
							dt_exportacao = clock_timestamp(),
							nm_usuario_exportacao = nm_usuario_export_p
					where nr_sequencia = c01_w.nr_sequencia;
					
					commit;		
				
				exception when others then
					rollback;
				end;
		
		end;
		
end loop;
close C01;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
