CREATE OR REPLACE PROCEDURE baca_indicador_gestao () AS $body$
DECLARE

nr_sequencia_w			bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	indicador_gestao
where 	nr_seq_wheb in (876, 877, 878, 879, 880, 881, 882, 883, 906)
and 	ds_tabela_visao in ('eis_manutencao_ti_v', 'eis_nutricao_v');


BEGIN
open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		delete FROM INDICADOR_GESTAO_PERFIL  where nr_seq_indicador = nr_sequencia_w;
		delete FROM IND_GESTAO_DOC_CLIENTE 	where nr_seq_indicador = nr_sequencia_w;
		delete FROM INDIC_GESTAO_QUAL 	where nr_seq_indicador  = nr_sequencia_w;
		delete FROM INDICADOR_GESTAO_DOC 	where nr_seq_indicador = nr_sequencia_w;
		delete FROM INDICADOR_GESTAO_RELAT 	where nr_seq_indicador = nr_sequencia_w;
		delete FROM SUBINDICADOR_GESTAO 	where nr_seq_indicador = nr_sequencia_w;
		delete FROM INDICADOR_GESTAO_ATRIB 	where nr_seq_ind_gestao = nr_sequencia_w;
                delete FROM INDICADOR_GESTAO 	where nr_sequencia = nr_sequencia_w;
	exception
	when others then
		nr_sequencia_w		:= nr_sequencia_w;
        end;
end loop;
close c01;
commit;

end;
