CREATE OR REPLACE PROCEDURE baca_insere_tipo_prestador () AS $body$
DECLARE


nr_sequencia_w			bigint;
nr_seq_tipo_prestador_w		bigint;
nm_usuario_w			varchar(15);
qt_registro_w			integer	:= 0;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_tipo_prestador,
		nm_usuario
	from	pls_prestador
	where	ie_situacao	= 'A'
	and	(nr_seq_tipo_prestador IS NOT NULL AND nr_seq_tipo_prestador::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_tipo_prestador_w,
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	pls_prestador_tipo
	where	nr_seq_prestador	= nr_sequencia_w
	and	nr_seq_tipo		= nr_seq_tipo_prestador_w;

	if (qt_registro_w	= 0) then
		insert into pls_prestador_tipo(nr_sequencia, nr_seq_prestador, nr_seq_tipo,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			nm_usuario_nrec)
		values (	nextval('pls_prestador_tipo_seq'), nr_sequencia_w, nr_seq_tipo_prestador_w,
			clock_timestamp(), nm_usuario_w, clock_timestamp(),
			nm_usuario_w);
	end if;
	end;
end loop;
close C01;

commit;

end;
