CREATE OR REPLACE PROCEDURE baca_inspecao_receb_lote () AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_lote_fornec_w	varchar(15);
dt_validade_w		timestamp;
qt_inspecao_w		double precision;
nm_usuario_w		varchar(15);
qt_count_w		bigint;

c01 CURSOR FOR

SELECT	nr_sequencia,
	ds_lote_fornec,
	dt_validade,
	qt_inspecao,
	nm_usuario
from	inspecao_recebimento
where	(ds_lote_fornec IS NOT NULL AND ds_lote_fornec::text <> '')
and	(dt_validade IS NOT NULL AND dt_validade::text <> '')
and	(qt_inspecao IS NOT NULL AND qt_inspecao::text <> '');


BEGIN

open C01;
loop
fetch C01
into	nr_sequencia_w,
	ds_lote_fornec_w,
	dt_validade_w,
	qt_inspecao_w,
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_count_w
	from	inspecao_recebimento_lote
	where	nr_seq_inspecao		= nr_sequencia_w
	and	cd_lote_fabricacao	= ds_lote_fornec_w
	and	dt_validade		= dt_validade_w
	and	qt_material		= qt_inspecao_w;

	if (qt_count_w = 0) then
		insert into inspecao_recebimento_lote(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_seq_inspecao,
			dt_validade,
			qt_material,
			cd_lote_fabricacao,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		values (	nextval('inspecao_recebimento_lote_seq'),
			clock_timestamp(),
			nm_usuario_w,
			nr_sequencia_w,
			dt_validade_w,
			qt_inspecao_w,
			ds_lote_fornec_w,
			clock_timestamp(),
			nm_usuario_w);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
