CREATE OR REPLACE PROCEDURE baca_laudo_pac_proc () AS $body$
DECLARE


nr_registros_w			bigint	:= 0;
nr_atendimento_w		bigint	:= 0;
nr_sequencia_w			bigint	:= 0;
nr_seq_proc_w			bigint	:= 0;
nr_prescricao_w			bigint	:= 0;
nr_sequencia_proc_w		bigint	:= 0;


C000 CURSOR FOR
SELECT
	nr_atendimento,
	coalesce(nr_prescricao,0),
	nr_sequencia,
	nr_seq_proc
from	laudo_paciente a
where	(nr_seq_proc IS NOT NULL AND nr_seq_proc::text <> '')
  and	not exists (select 1
	from	Procedimento_paciente b
	where	a.nr_sequencia	= b.nr_laudo);

C001 CURSOR FOR
SELECT	nr_sequencia
from	procedimento_paciente
where	nr_prescricao	= nr_prescricao_w
  and	nr_sequencia	<> nr_seq_proc_w
  and	coalesce(nr_laudo::text, '') = '';



BEGIN

nr_registros_w	       := 0;
OPEN C000;
LOOP
FETCH C000 INTO
	nr_atendimento_w,
	nr_prescricao_w,
	nr_sequencia_w,
	nr_seq_proc_w;
	EXIT WHEN NOT FOUND; /* apply on c000 */
	begin

	update procedimento_paciente
	set nr_laudo		= nr_sequencia_w
	where nr_sequencia	= nr_seq_proc_w;

	if (nr_prescricao_w > 0) then
		begin
		OPEN C001;
		LOOP
		FETCH C001 INTO nr_sequencia_Proc_w;
		EXIT WHEN NOT FOUND; /* apply on c001 */
			update	procedimento_paciente
			set 	nr_laudo = nr_sequencia_w
			where	nr_sequencia	= nr_sequencia_proc_w;
		END LOOP;
		CLOSE C001;
		end;
	end if;
	nr_registros_w := nr_registros_w + 1;
	/*if  (nr_registros_w /1000) = trunc(nr_registros_w / 1000) then
		begin
		insert into logxxxx_tasy values(sysdate, 'Marcus', 88, nr_registros_w);
		commit;
		end;
	end if;*/
	end;
END LOOP;
CLOSE C000;
commit;
end;
$body$
