CREATE OR REPLACE PROCEDURE baca_longa_permanencia (NR_SEQ_PROTOCOLO_P bigint) AS $body$
DECLARE

 
 
nr_atendimento_w		bigint;
nr_interno_conta_w	bigint;

 
C01 CURSOR FOR 
SELECT 	nr_atendimento, 
		nr_interno_conta 
from		conta_paciente 
where		nr_seq_protocolo	= nr_seq_protocolo_p 
order by 	nr_interno_conta;


BEGIN 
 
/* Calcular longa permanencia */
 
open c01;
loop 
	fetch 	c01 into 
			nr_atendimento_w, 
			nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
			BEGIN 
			CALL gravar_longa_permanencia(nr_atendimento_w,nr_interno_conta_w,'Tasy');
			END;
END LOOP;
close c01;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
