SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_mapa_estoque () AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_item_w		bigint;
nr_seq_coluna_w		bigint;
nr_seq_coluna_ww	bigint;
ie_entrada_saida_w	varchar(1);

/* procedure criada por Matheus em 10/02/2007 para atualizar a nova forma do cadastro do Mapa de Estoque - Alteração HSL*/

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	mapa_estoque
	order by 1;

C02 CURSOR FOR
	SELECT	nr_seq_coluna,
		ie_entrada_saida,
		nr_sequencia
	from	mapa_est_item
	where	nr_seq_mapa = nr_sequencia_w
	order by 2,1;


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	nr_seq_coluna_ww	:= 1;
	open c02;
	loop
	fetch c02 into
		nr_seq_coluna_w,
		ie_entrada_saida_w,
		nr_seq_item_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		update	mapa_est_item
		set	nr_seq_coluna	 = nr_seq_coluna_ww,
			ie_entrada_saida = 'E',
			nm_usuario	 = 'BacaMapa',
			dt_atualizacao	 = clock_timestamp()
		where	nr_seq_mapa	 = nr_sequencia_w
		and	nr_sequencia 	 = nr_seq_item_w;

		nr_seq_coluna_ww	:= nr_seq_coluna_ww + 1;
		end;
	end loop;
	close c02;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_mapa_estoque () FROM PUBLIC;

