SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_material_falta_estab ( cd_estabelecimento_p bigint) AS $body$
DECLARE


count_w		smallint;
cd_estabelecimento_w	smallint;
cd_material_w		integer;
qt_falta_w		double precision;
ds_motivo_w		varchar(255);
ie_solucao_w		varchar(1);
dt_falta_w		timestamp;


C01 CURSOR FOR
	SELECT	cd_estabelecimento
	FROM	estabelecimento
	where	cd_estabelecimento <> cd_estabelecimento_p;

C02 CURSOR FOR
	SELECT	a.cd_material,
		a.qt_falta,
		a.ds_motivo,
		a.ie_solucao,
		a.dt_falta
	FROM	material_falta a
	where	coalesce(a.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p
	and not exists (
		SELECT	1
		from	material_falta b
		where	b.cd_material = a.cd_material
		and	b.cd_estabelecimento = cd_estabelecimento_w);

BEGIN

OPEN C01;
LOOP
FETCH c01 INTO
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	BEGIN
	OPEN C02;
	LOOP
	FETCH c02 INTO
		cd_material_w,
		qt_falta_w,
		ds_motivo_w,
		ie_solucao_w,
		dt_falta_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		BEGIN
		IF (count_w > 1000) THEN
			count_w	:= 0;
			COMMIT;
		END IF;
		INSERT INTO material_falta(
			NR_SEQUENCIA,
			CD_MATERIAL,
			DT_ATUALIZACAO,
			NM_USUARIO,
			QT_FALTA,
			DS_MOTIVO,
			IE_SOLUCAO,
			DT_ATUALIZACAO_NREC,
			NM_USUARIO_NREC,
			DT_FALTA,
			CD_ESTABELECIMENTO)
		VALUES ( nextval('material_falta_seq'),
			cd_material_w,
			clock_timestamp(),
			'TasyImportEstab',
			qt_falta_w,
			ds_motivo_w,
			ie_solucao_w,
			clock_timestamp(),
			'TasyImportEstab',
			dt_falta_w,
			cd_estabelecimento_w);
		end;
	end loop;
	close c02;
	end;
end loop;
close c01;

COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
