DECLARE


/*
Baca para ajustar a integridade entre MATERIAL_ATEND_PACIENTE e ATEND_PACIENTE_UNIDADE
*/
nr_seq_atepacu_w	bigint;
nr_atendimento_w	bigint;
cd_setor_atendimento_w	integer;
dt_entrada_unidade_w	timestamp;
nr_seq_interno_w	bigint;

c01 CURSOR FOR
	SELECT	distinct a.nr_seq_atepacu,
		a.nr_atendimento
	from	material_atend_paciente a
	where	not exists (	SELECT	1
				from	atend_paciente_unidade b
				where	a.nr_seq_atepacu	= b.nr_seq_interno)
	and	exists (		select	1
				from	atend_paciente_unidade c
				where	a.nr_atendimento	= c.nr_atendimento);


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_seq_atepacu_w,
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	max(cd_setor_atendimento),
		max(dt_entrada_unidade),
		max(nr_seq_interno)
	into STRICT	cd_setor_atendimento_w,
		dt_entrada_unidade_w,
		nr_seq_interno_w
	from	atend_paciente_unidade
	where	nr_atendimento	= nr_atendimento_w;

	update	material_atend_paciente
	set	dt_entrada_unidade	= dt_entrada_unidade_w,
		cd_setor_atendimento	= cd_setor_atendimento_w,
		nr_seq_atepacu		= nr_seq_interno_w
	where	nr_atendimento		= nr_atendimento_w
	and	nr_seq_atepacu		= nr_seq_atepacu_w;

	end;
END LOOP;
