CREATE OR REPLACE PROCEDURE baca_movto_banco_pend () AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from 	MOVTO_BANCO_PEND a
where 	not exists (SELECT	1
	from	MOVTO_TRANS_FINANC b
	where 	a.NR_SEQ_MOVTO_TRANS_FIN = b.NR_SEQUENCIA)
and 	(a.NR_SEQ_MOVTO_TRANS_FIN IS NOT NULL AND a.NR_SEQ_MOVTO_TRANS_FIN::text <> '');

if (qt_registros_w > 0) then

	CALL Exec_Sql_Dinamico('MOVBANPEN_MOVTRFI_FK',	' update MOVTO_BANCO_PEND a set a.NR_SEQ_MOVTO_TRANS_FIN = null '||
							' where	not exists (select 1 from MOVTO_TRANS_FINANC b where a.NR_SEQ_MOVTO_TRANS_FIN = b.NR_SEQUENCIA) '||
							' and a.NR_SEQ_MOVTO_TRANS_FIN 	is not null ');

	CALL Exec_Sql_Dinamico('MOVBANPEN_MOVTRFI_FK', 	' Alter Table MOVTO_BANCO_PEND drop Constraint MOVBANPEN_MOVTRFI_FK ');

	CALL Exec_Sql_Dinamico('MOVBANPEN_MOVTRFI_FK', 	' Alter Table MOVTO_BANCO_PEND add (Constraint MOVBANPEN_MOVTRFI_FK Foreign Key (NR_SEQ_MOVTO_TRANS_FIN) '||
							' References MOVTO_TRANS_FINANC (NR_SEQUENCIA)) ');
end if;

end;
