SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_nec_atualiz_migr_os () AS $body$
DECLARE


nr_seq_os_w          	bigint;
ie_situacao_migracao_w	varchar(1);

c01 CURSOR FOR
SELECT	nr_seq_os,
	obter_situacao_funcao_migracao(cd_funcao) ie_situacao_migracao
from 	os_desenv_tec_v
group by
	nr_seq_os,
	cd_funcao;


BEGIN
open c01;
loop
fetch c01 into	nr_seq_os_w,
		ie_situacao_migracao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
     begin
     update  man_ordem_servico
     set     ie_atualizacao_migracao = CASE WHEN ie_situacao_migracao_w='I' THEN 'I'  ELSE 'P' END ,
             dt_atualizacao_migracao = clock_timestamp(),
             nm_usuario_atual_migr = 'Portuga',
             dt_atualizacao = clock_timestamp(),
             nm_usuario = 'Portuga'
     where   nr_sequencia = nr_seq_os_w;
     end;
end loop;
close c01;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
