SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_nm_regra_combinacao_item () AS $body$
DECLARE

 
tb_nr_seq_comb_tm_w	pls_util_cta_pck.t_number_table;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		nm_regra 
	from	pls_combinacao_item_cta;
	
C02 CURSOR(	nr_seq_combinacao_pc	pls_combinacao_item_cta.nr_sequencia%type) FOR 
	SELECT	nr_sequencia 
	from	pls_combinacao_item_tm 
	where	nr_seq_combinacao = nr_seq_combinacao_pc;

BEGIN 
 
for r_c01_w in C01 loop 
 
	tb_nr_seq_comb_tm_w.delete;
	 
	open c02(r_c01_w.nr_sequencia);
	loop 
		fetch c02 bulk collect into tb_nr_seq_comb_tm_w 
		limit pls_util_pck.qt_registro_transacao_w;
		exit when tb_nr_seq_comb_tm_w.count = 0;
		 
		forall i in tb_nr_seq_comb_tm_w.first..tb_nr_seq_comb_tm_w.last 
			update	pls_combinacao_item_tm 
			set	nm_regra = r_c01_w.nm_regra 
			where	nr_sequencia = tb_nr_seq_comb_tm_w(i);
		commit;
	end loop;
	close c02;
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
