SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_nr_titulo_pagar_ret () AS $body$
DECLARE

 
tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
tb_nr_titulo_w		pls_util_cta_pck.t_number_table;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		nr_titulo 
	from	pls_lote_ret_trib_valor;


BEGIN 
-- foi criado o campo nr_titulo_pagar_ret na tabela pls_pag_prest_venc_trib, o mesmo será alimentado com o numero do titulo 
-- a pagar do lote de retenção, quando o título do lote for gerado. Para alimentar inicialmente este campo foi criado este baca. 
open C01;
loop 
	fetch C01 bulk collect into tb_nr_sequencia_w, tb_nr_titulo_w 
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;
 
	forall	i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last 
		update	pls_pag_prest_venc_trib 
		set	nr_titulo_pagar_ret = tb_nr_titulo_w(i) 
		where 	nr_seq_lote_ret_trib_valor = tb_nr_sequencia_w(i);
	commit;
end loop;
close C01;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_nr_titulo_pagar_ret () FROM PUBLIC;

