SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_orcamento_custo_pk () AS $body$
DECLARE


cd_estabelecimento_w	bigint;
cd_tabela_custo_w	bigint;
cd_centro_controle_w	bigint;
cd_natureza_gasto_w	numeric(20);
qt_registro_w		bigint;
qt_reg_nulo_w		bigint;

c01 CURSOR FOR
SELECT	cd_estabelecimento,
	cd_tabela_custo,
	cd_centro_controle,
	cd_natureza_gasto
from	orcamento_custo
where	coalesce(nr_sequencia::text, '') = ''
order by 1,2,3,4;


BEGIN
qt_registro_w	:= 0;

/* Atualiza_tipo_gasto */

CALL baca_tipo_gasto();

/* Atualiza NR_SEQUENCIA - nova PK*/

open C01;
loop
fetch C01 into
	cd_estabelecimento_w,
	cd_tabela_custo_w,
	cd_centro_controle_w,
	cd_natureza_gasto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_registro_w		:= qt_registro_w + 1;

	update	orcamento_custo
	set	nr_sequencia	= nextval('orcamento_custo_seq')
	where	cd_estabelecimento	= cd_estabelecimento_w
	and	cd_tabela_custo		= cd_tabela_custo_w
	and	cd_centro_controle	= cd_centro_controle_w
	and	cd_natureza_gasto	= cd_natureza_gasto_w;

	if (qt_registro_w >= 5000) then
		qt_registro_w	:= 0;
		commit;
	end if;
	end;
end loop;
close C01;
commit;

select	count(*)
into STRICT	qt_reg_nulo_w
from	orcamento_custo
where	coalesce(nr_sequencia::text, '') = '';

select	count(*)
into STRICT	qt_registro_w
from	user_cons_columns
where	constraint_name = 'ORCCUST_PK'
and	column_name	= 'NR_SEQUENCIA';

if (qt_registro_w = 0) and (qt_reg_nulo_w = 0) then
	CALL exec_sql_dinamico('ORCCUST_PK','alter table ORCAMENTO_CUSTO drop constraint ORCCUST_PK');
	CALL exec_sql_dinamico('ORCCUST_PK','drop index ORCCUST_PK');
	CALL exec_sql_dinamico('ORCCUST_PK','alter table ORCAMENTO_CUSTO modify nr_sequencia not null');
	CALL exec_sql_dinamico('ORCCUST_PK','alter table ORCAMENTO_CUSTO modify ie_tipo_gasto not null');
	CALL exec_sql_dinamico('ORCCUST_PK','drop trigger orcamento_custo_insert');
	CALL exec_sql_dinamico('ORCCUST_PK','alter table ORCAMENTO_CUSTO add (constraint ORCCUST_PK Primary Key (NR_SEQUENCIA) using index tablespace TASY_INDEX)');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
