DECLARE


qt_reg_w	bigint;
rowid_w		varchar(50);
ds_comando_w	varchar(200);

C01 CURSOR FOR
	SELECT oid
	from orcamento_paciente_proc
	where coalesce(nr_sequencia::text, '') = ''  LIMIT 1000;


BEGIN


select count(*)
into STRICT qt_reg_w
from orcamento_paciente_proc
where coalesce(nr_sequencia::text, '') = '';

while(qt_reg_w > 0) loop
	open c01;
	loop
		fetch c01 into rowid_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */

		update orcamento_paciente_proc
		set nr_sequencia = nextval('orcamento_paciente_proc_seq')
		where rowid = rowid_w;
	end loop;
	close c01;

	commit;

	select count(*)
	into STRICT qt_reg_w
	from orcamento_paciente_proc
	where coalesce(nr_sequencia::text, '') = '';
end loop;

ds_comando_w := 'alter table orcamento_paciente_proc drop constraint orcpapr_pk';

/*execute immediate ds_comando_w;*/

CALL tasy_sincronizar_base();

end;
