CREATE OR REPLACE PROCEDURE baca_ordem_compra_nota_fiscal ( nr_sequencia_p bigint, /*sequencia da nota fiscal*/
 nm_usuario_p text) AS $body$
DECLARE



nr_ordem_compra_w		bigint;
nr_item_oci_w			integer;
cd_estabelecimento_w		smallint;
cd_cgc_emitente_w		varchar(14);
cd_serie_nf_w			varchar(5);
nr_sequencia_nf_w		smallint;
nr_item_nf_w			integer;
cd_material_w			integer;
dt_entrega_ordem_w		timestamp;
qt_item_estoque_w		double precision;
nr_nota_fiscal_w		varchar(255);

c01 CURSOR FOR
SELECT	distinct
	b.nr_ordem_compra,
	b.nr_item_oci,
	a.cd_estabelecimento,
	a.cd_cgc_emitente,
	a.cd_serie_nf,
	a.nr_sequencia_nf,
	b.nr_item_nf,
	b.cd_material,
	b.dt_entrega_ordem,
	b.qt_item_estoque,
	a.nr_nota_fiscal
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia  = b.nr_sequencia
and	a.nr_sequencia  = nr_sequencia_p
and not exists (
	SELECT  1
	from    ordem_compra_nota_fiscal x
	where   x.nr_Sequencia  = a.nr_sequencia
	and	x.nr_item_nf    = b.nr_item_nf);


BEGIN

open C01;
loop
fetch C01 into
	nr_ordem_compra_w,
	nr_item_oci_w,
	cd_estabelecimento_w,
	cd_cgc_emitente_w,
	cd_serie_nf_w,
	nr_sequencia_nf_w,
	nr_item_nf_w,
	cd_material_w,
	dt_entrega_ordem_w,
	qt_item_estoque_w,
	nr_nota_fiscal_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into ordem_compra_nota_fiscal(
		nr_ordem_compra,
		nr_item_oci,
		cd_estabelecimento,
		cd_cgc_emitente,
		cd_serie_nf,
		nr_sequencia_nf,
		nr_item_nf,
		cd_material,
		dt_entrega,
		qt_entrega,
		dt_atualizacao,
		nm_usuario,
		nr_sequencia,
		ie_status_exportar,
		nr_nota_fiscal)
	values (	nr_ordem_compra_w,
		nr_item_oci_w,
		cd_estabelecimento_w,
		cd_cgc_emitente_w,
		cd_serie_nf_w,
		nr_sequencia_nf_w,
		nr_item_nf_w,
		cd_material_w,
		dt_entrega_ordem_w,
		qt_item_estoque_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		'N',
		nr_nota_fiscal_w);
	end;
end loop;
close C01;

commit;

end;
$body$
