CREATE OR REPLACE PROCEDURE baca_os_rev_teste_migr_dupl () AS $body$
DECLARE

 
nr_seq_projeto_w	bigint;
nr_seq_os_w		bigint;

c01 CURSOR FOR 
SELECT	x.nr_seq_projeto, 
	x.nr_seq_os	 
from 	os_usuario_v u, 
	os_revisao_teste_migracao_v x 
where 	u.nr_seq_os = x.nr_seq_os 
and 	exists ( 
		SELECT	1 
		from 	os_revisao_teste_migracao_v r 
		where 	x.nr_seq_projeto = r.nr_seq_projeto 
		and 	x.nr_seq_os <> r.nr_seq_os 
		and	r.nr_seq_estagio_os(2,9)) 
group by 
	x.nr_seq_projeto, 
	x.nr_seq_os 
order by 
	1,2;
	

BEGIN 
open c01;
loop 
fetch c01 into	nr_seq_projeto_w, 
		nr_seq_os_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	update	man_ordem_servico 
	set	nr_seq_tipo_solucao = 91, 
		ie_grau_satisfacao = 'N', 
		nm_usuario = 'b_e_os_r_t_m_d', 
		dt_atualizacao = clock_timestamp() 
	where	nr_sequencia = nr_seq_os_w;
		 
	CALL man_encerrar_os(nr_seq_os_w, 9, 'b_e_os_r_t_m_d', null);
	end;
end loop;
close c01;
commit;
end;
