CREATE OR REPLACE PROCEDURE baca_pacote () AS $body$
DECLARE


nr_rowid_w			oid;
nr_sequencia_w		bigint;

C000 CURSOR FOR
SELECT oid
from  pacote_tipo_acomodacao
where coalesce(nr_sequencia::text, '') = '';



BEGIN

OPEN C000;
LOOP
FETCH C000 INTO nr_rowid_w;
	EXIT WHEN NOT FOUND; /* apply on c000 */
	begin
	select nextval('pacote_tipo_acomodacao_seq')
	into STRICT	nr_sequencia_w
	;

	update pacote_tipo_acomodacao
	set nr_sequencia	= nr_sequencia_w
	where rowid		= nr_rowid_w;
	end;
END LOOP;
CLOSE C000;
commit;
END;
