SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_parcela_baixa_cartao () AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_movto_w		bigint;
nr_seq_parcela_w	bigint;
ds_observacao_w		varchar(255);
pos_w			bigint;
ds_data_w		varchar(255);
dt_parcela_w		timestamp;
cont_w			bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.nr_seq_movto,
	a.ds_observacao
from	movto_cartao_cr_baixa a
where	coalesce(a.nr_seq_parcela::text, '') = ''
order by coalesce(a.dt_atualizacao_nrec,dt_atualizacao);


BEGIN

CALL exec_sql_dinamico('Tasy','create table bkp_movto_cartao_cr_baixa2 as (select * from movto_cartao_cr_baixa)');

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	nr_seq_movto_w,
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select	count(*),
		max(nr_sequencia)
	into STRICT	cont_w,
		nr_seq_parcela_w
	from	movto_cartao_cr_parcela
	where	nr_seq_movto	= nr_seq_movto_w;

	if (cont_w	= 1) then
		update	movto_cartao_cr_baixa
		set	nr_seq_parcela	= nr_seq_parcela_w
		where	nr_seq_movto	= nr_seq_movto_w
		and	coalesce(nr_seq_parcela::text, '') = '';

		CALL atualizar_saldo_cartao_parcela(nr_seq_parcela_w,'Tasy');
	end if;

	pos_w			:= 0;
	ds_data_w		:= null;
	dt_parcela_w		:= null;
	nr_seq_parcela_w	:= null;

	if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') and (cont_w > 1) then
		select	position(' de ' in ds_observacao_w)
		into STRICT	pos_w
		;

		if (pos_w > 0) then
			select	substr(ds_observacao_w,pos_w + 4,length(ds_observacao_w))
			into STRICT	ds_data_w
			;

			if (length(ds_data_w) = 8) then
				dt_parcela_w	:= to_date(ds_data_w,'dd/mm/yy');
			elsif (length(ds_data_w) = 10) then
				dt_parcela_w	:= to_date(ds_data_w,'dd/mm/yyyy');
			end if;
		end if;

		if (dt_parcela_w IS NOT NULL AND dt_parcela_w::text <> '') then

			select	min(nr_sequencia)
			into STRICT	nr_seq_parcela_w
			from	movto_cartao_cr_parcela
			where	dt_parcela	= dt_parcela_w
			and	nr_seq_movto	= nr_seq_movto_w;

			if (nr_seq_parcela_w IS NOT NULL AND nr_seq_parcela_w::text <> '') then

				update	movto_cartao_cr_baixa
				set	nr_seq_parcela	= nr_seq_parcela_w
				where	nr_sequencia	= nr_sequencia_w;

				CALL atualizar_saldo_cartao_parcela(nr_seq_parcela_w,'Tasy');
			end if;
		end if;
	end if;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
