CREATE OR REPLACE PROCEDURE baca_parto () AS $body$
DECLARE


nr_atendimento_w	integer;
qt_nasc_mortos_w	bigint;
qt_nasc_vivos_w	bigint;

c01 CURSOR FOR
	SELECT nr_atendimento
	from	nascimento
	where	dt_atualizacao < to_date('13/01/2004','dd/mm/yyyy');



BEGIN

OPEN C01;
LOOP
FETCH C01 into	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	sum(CASE WHEN ie_unico_nasc_vivo='S' THEN  1  ELSE 0 END ),
  		sum(CASE WHEN ie_unico_nasc_vivo='N' THEN  1  ELSE 0 END )
	into STRICT	qt_nasc_vivos_w,
		qt_nasc_mortos_w
	from	nascimento
	where	nr_atendimento	= nr_atendimento_w;

	update	parto
	set	qt_nasc_vivos		= qt_nasc_vivos_w,
		qt_nasc_mortos	= qt_nasc_mortos_w
	where	nr_atendimento	= nr_atendimento_w;
	end;
END LOOP;
close c01;

END;
