SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pat_mes_calculado () AS $body$
DECLARE


dt_mes_ref_w		timestamp;
cd_estabelecimento_w	smallint;
dt_valor_bem_w	timestamp;
qt_registro_w		bigint;

c01 CURSOR FOR
	SELECT	dt_mes_ref,
		cd_estabelecimento
	from	pat_mes_ref
	order by cd_estabelecimento;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	pat_mes_ref a
where	a.ie_situacao = '0'
and	exists (SELECT	1
		from		pat_valor_bem x,
				pat_bem y
		where		x.nr_seq_bem = y.nr_sequencia
		and		a.cd_estabelecimento = y.cd_estabelecimento
		and		trunc(x.dt_valor,'month') = trunc(a.dt_mes_ref,'month'));

if (qt_registro_w > 0) then
	begin
	open C01;
	loop
	fetch C01
	into	dt_mes_ref_w,
		cd_estabelecimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	max(a.dt_valor)
		into STRICT	dt_valor_bem_w
		from	pat_valor_bem a,
			pat_bem b
		where	a.nr_seq_bem = b.nr_sequencia
		and	a.dt_valor = dt_mes_ref_w
		and	b.cd_estabelecimento = cd_estabelecimento_w;

		if (dt_valor_bem_w IS NOT NULL AND dt_valor_bem_w::text <> '') then
			update	pat_mes_ref
			set	ie_situacao = '1'
			where	dt_mes_ref = dt_mes_ref_w
			and	cd_estabelecimento = cd_estabelecimento_w;
		end if;
		end;
	end loop;
	close C01;
	commit;
	end;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
