CREATE OR REPLACE PROCEDURE baca_pat_tipo_hist_transf () AS $body$
DECLARE



nr_seq_tipo_hist_w		bigint;
qt_registro_w			integer;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_tipo_hist_w
from	pat_tipo_historico
where	ie_situacao = 'A'
and	ie_transferencia = 'S';

if (nr_seq_tipo_hist_w = 0) then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_tipo_hist_w
	from	pat_tipo_historico
	where	ie_transferencia = 'S';
end if;

select	count(*)
into STRICT	qt_registro_w
from	pat_doc_transferencia
where	coalesce(nr_seq_tipo::text, '') = '';

if (qt_registro_w > 0) then
	update	pat_doc_transferencia
	set	nr_seq_tipo = nr_seq_tipo_hist_w
	where	coalesce(nr_seq_tipo::text, '') = '';
end if;

end;
