SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pessoa_cobr () AS $body$
DECLARE


cont_w	bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	cobranca
where	coalesce(cd_pessoa_fisica::text, '') = ''
and	coalesce(cd_cgc::text, '') = ''
and ((nr_titulo IS NOT NULL AND nr_titulo::text <> '') or (nr_seq_cheque IS NOT NULL AND nr_seq_cheque::text <> ''));

while	cont_w > 0 loop

	begin
	update	cobranca
	set	dt_atualizacao = dt_atualizacao
	where	coalesce(cd_pessoa_fisica::text, '') = ''
	and	coalesce(cd_cgc::text, '') = ''
	and ((nr_titulo IS NOT NULL AND nr_titulo::text <> '') or (nr_seq_cheque IS NOT NULL AND nr_seq_cheque::text <> ''))  LIMIT 500;
	exception
	when others then
		null;
	end;

	cont_w	:= cont_w - 500;

	commit;

end loop;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
