CREATE OR REPLACE PROCEDURE baca_pessoa_fisica_laudo () AS $body$
DECLARE


nr_seq_laudo_w		bigint;
cd_pessoa_fisica_w	varchar(10);
qt_registro_w		bigint	:= 0;

C01 CURSOR FOR
	SELECT  a.nr_sequencia,
		b.cd_pessoa_fisica
	from   	atendimento_paciente b,
		laudo_paciente a
	where  	a.nr_atendimento = b.nr_atendimento
	and	coalesce(a.cd_pessoa_fisica::text, '') = '';


BEGIN

CALL wheb_usuario_pck.SET_IE_EXECUTAR_TRIGGER('N');

open C01;
loop
fetch C01 into
	nr_seq_laudo_w,
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
		update	laudo_paciente
		set	cd_pessoa_fisica = cd_pessoa_fisica_w
		where	nr_sequencia   = nr_seq_laudo_w;

		qt_registro_w	:= qt_registro_w + 1;

		if (qt_registro_w	= 5000) then
			qt_registro_w	:= 0;
			commit;
		end if;

	end if;
	end;
end loop;

close C01;

commit;

CALL wheb_usuario_pck.SET_IE_EXECUTAR_TRIGGER('S');

end;
