CREATE OR REPLACE PROCEDURE baca_pessoa_juridica_estab ( nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			integer;
qt_insert_w			integer;
cd_estabelecimento_w		integer;
cd_cgc_w			varchar(14);
nm_pessoa_contato_w		varchar(255);
nr_ramal_contato_w		integer;
ds_email_w			varchar(255);
cd_cond_pagto_w		bigint;
qt_dia_prazo_entrega_w	smallint;
vl_minimo_nf_w		double precision;
ie_qualidade_w		varchar(3);
cd_referencia_fornec_w	varchar(20);

c01 CURSOR FOR
	SELECT	cd_estabelecimento
	from	estabelecimento;

c02 CURSOR FOR
	SELECT	cd_cgc,
		nm_pessoa_contato,
		nr_ramal_contato,
		ds_email,
		cd_cond_pagto,
		qt_dia_prazo_entrega,
		vl_minimo_nf,
		ie_qualidade,
		cd_referencia_fornec
	from	pessoa_juridica
	where ((nm_pessoa_contato IS NOT NULL AND nm_pessoa_contato::text <> '') or
		(nr_ramal_contato IS NOT NULL AND nr_ramal_contato::text <> '') or
		(ds_email IS NOT NULL AND ds_email::text <> '') or
		(cd_cond_pagto IS NOT NULL AND cd_cond_pagto::text <> '') or
		(qt_dia_prazo_entrega IS NOT NULL AND qt_dia_prazo_entrega::text <> '') or
		(vl_minimo_nf IS NOT NULL AND vl_minimo_nf::text <> '') or
		(ie_qualidade IS NOT NULL AND ie_qualidade::text <> '') or
		(cd_referencia_fornec IS NOT NULL AND cd_referencia_fornec::text <> ''));


BEGIN
qt_insert_w	:= 0;

open c01;
loop
fetch c01 into
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	pessoa_juridica_estab
	where	cd_estabelecimento = cd_estabelecimento_w;
	if (qt_existe_w = 0) then
		begin
		open c02;
		loop
		fetch c02 into
			cd_cgc_w,
			nm_pessoa_contato_w,
			nr_ramal_contato_w,
			ds_email_w,
			cd_cond_pagto_w,
			qt_dia_prazo_entrega_w,
			vl_minimo_nf_w,
			ie_qualidade_w,
			cd_referencia_fornec_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			qt_insert_w	:= qt_insert_w + 1;
			if (qt_insert_w >= 2000) then
				qt_insert_w := 0;
				commit;
			end if;
			insert into pessoa_juridica_estab(
				nr_sequencia,
				cd_cgc,
				cd_estabelecimento,
				dt_atualizacao,
				nm_usuario,
				nm_pessoa_contato,
				nr_ramal_contato,
				ds_email,
				cd_cond_pagto,
				qt_dia_prazo_entrega,
				vl_minimo_nf,
				ie_qualidade,
				cd_referencia_fornec,
				IE_CONTA_DIF_NF)
			values ( nextval('pessoa_juridica_estab_seq'),
				cd_cgc_w,
				cd_estabelecimento_w,
				clock_timestamp(),
				nm_usuario_p,
				nm_pessoa_contato_w,
				nr_ramal_contato_w,
				ds_email_w,
				cd_cond_pagto_w,
				qt_dia_prazo_entrega_w,
				vl_minimo_nf_w,
				ie_qualidade_w,
				cd_referencia_fornec_w,
				'N');
			commit;
			end;
		end loop;
		close c02;
		end;
	end if;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
