SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_acerto_material_bras () AS $body$
DECLARE


cd_material_w		bigint;
cd_tiss_brasindice_w	varchar(20);
ds_medicamento_w	varchar(255);
nr_sequencia_w		bigint;

c01 CURSOR FOR
	SELECT	cd_material,
		nr_sequencia
	from	pls_material
	where	(cd_tiss_brasindice IS NOT NULL AND cd_tiss_brasindice::text <> '')
	and	coalesce(ds_medicamento::text, '') = '';


BEGIN

open c01;
loop
fetch c01 into
	cd_material_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	max(substr(tiss_obter_cod_brasindice(cd_apresentacao, cd_laboratorio, cd_medicamento),1,15)) cd_tiss_brasindice
	into STRICT	cd_tiss_brasindice_w
	from	material_brasindice
	where	cd_material	= cd_material_w;

	select	substr(pls_obter_desc_mat_bras_tiss(cd_tiss_brasindice_w),1,255) ds_medicamento
	into STRICT	ds_medicamento_w
	;

	update	pls_material
	set	ds_medicamento	= ds_medicamento_w
	where	nr_sequencia	= nr_sequencia_w;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_pls_acerto_material_bras () FROM PUBLIC;

