SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_adicionar_glosa_atend ( cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_registro_w		bigint;
nr_seq_motivo_glosa_w	bigint;



BEGIN

select	count(1)
into STRICT	qt_registro_w
from	tiss_motivo_glosa
where	cd_motivo_tiss = '9917';

if (qt_registro_w = 0) then
	insert into tiss_motivo_glosa(	nr_sequencia, dt_atualizacao, nm_usuario,
					dt_atualizacao_nrec, nm_usuario_nrec, ie_grupo_glosa,
					cd_motivo_tiss, ds_motivo_tiss, ie_acao_pls,
					cd_convenio, cd_motivo_glosa, ie_fechar_conta,
					ie_importacao_ops)
				values (	nextval('tiss_motivo_glosa_seq'), clock_timestamp(), 'ejsilva',
					clock_timestamp(), 'ejsilva', 22,
					'9917', 'Profissional não credenciado na operadora', 'D',
					null,null, 'S',
					'S');
end if;

select	count(1)
into STRICT	qt_registro_w
from	tiss_motivo_glosa
where	cd_motivo_tiss = '9920';

if (qt_registro_w = 0) then
	select	nextval('tiss_motivo_glosa_seq')
	into STRICT	nr_seq_motivo_glosa_w
	;

	insert into tiss_motivo_glosa(	nr_sequencia, dt_atualizacao, nm_usuario,
					dt_atualizacao_nrec, nm_usuario_nrec, ie_grupo_glosa,
					cd_motivo_tiss, ds_motivo_tiss, ie_acao_pls,
					cd_convenio, cd_motivo_glosa, ie_fechar_conta,
					ie_importacao_ops)
				values (	nr_seq_motivo_glosa_w, clock_timestamp(), 'acjjunior',
					clock_timestamp(), 'acjjunior', 22,
					'9920', 'Procedimento ou material, inativo ou fora de vigência', 'S',
					null,null, 'S',
					'S');

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_glosa_evento
	where	nr_seq_motivo_glosa = nr_seq_motivo_glosa_w
	and	ie_evento = 'CG';

	if (qt_registro_w = 0) then
		CALL Tasy_posicionar_sequence('PLS_GLOSA_EVENTO','NR_SEQUENCIA','N');
		CALL pls_inserir_evento_glosa(nr_seq_motivo_glosa_w,'CG','acjjunior');
	end if;
end if;

/* Lepinski - OS 420442 - Glosa "Cooperativa suspensa" */

select	count(1)
into STRICT	qt_registro_w
from	tiss_motivo_glosa
where	cd_motivo_tiss = '9921';

if (qt_registro_w = 0) then
	select	nextval('tiss_motivo_glosa_seq')
	into STRICT	nr_seq_motivo_glosa_w
	;

	insert into tiss_motivo_glosa(	nr_sequencia, dt_atualizacao, nm_usuario,
					dt_atualizacao_nrec, nm_usuario_nrec, ie_grupo_glosa,
					cd_motivo_tiss, ds_motivo_tiss, ie_acao_pls,
					cd_convenio, cd_motivo_glosa, ie_fechar_conta,
					ie_importacao_ops)
				values (	nr_seq_motivo_glosa_w, clock_timestamp(), 'rlepinski',
					clock_timestamp(), 'rlepinski', 22,
					'9921', 'Cooperativa suspensa', 'D',
					null,null, 'N',
					'S');

	CALL pls_inserir_evento_glosa(nr_seq_motivo_glosa_w,'CG','rlepinski');
	CALL pls_inserir_evento_glosa(nr_seq_motivo_glosa_w,'CC','rlepinski');
end if;

commit;

end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
