CREATE OR REPLACE PROCEDURE baca_pls_adicionar_glosa_desp ( cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_registro_w		bigint;
nr_seq_motivo_glosa_w	bigint;



BEGIN


select	max(nr_sequencia)
