					cd_motivo_tiss, ds_motivo_tiss, ie_acao_pls,
					cd_convenio, cd_motivo_glosa, ie_fechar_conta,
					ie_importacao_ops)
				values (	nr_seq_motivo_glosa_w, clock_timestamp(), 'dgkorz',
					clock_timestamp(), 'dgkorz', 22,
					'9920', 'Procedimento ou material, inativo ou fora de vigÃªncia', 'S',
					null,null, 'S',
					'S');
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_glosa_evento
	where	nr_seq_motivo_glosa = nr_seq_motivo_glosa_w
	and	ie_evento = 'CC';

	if (qt_registro_w = 0) then
		CALL Tasy_posicionar_sequence('PLS_GLOSA_EVENTO','NR_SEQUENCIA','N');
		CALL pls_inserir_evento_glosa(nr_seq_motivo_glosa_w,'CC','dgkorz');
	end if;

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_glosa_evento
	where	nr_seq_motivo_glosa = nr_seq_motivo_glosa_w
	and	ie_evento = 'DC';

	if (qt_registro_w = 0) then
		--Tasy_posicionar_sequence('PLS_GLOSA_EVENTO','NR_SEQUENCIA','N');
		CALL pls_inserir_evento_glosa(nr_seq_motivo_glosa_w,'DC','dgkorz');
	end if;
else
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_glosa_evento
	where	nr_seq_motivo_glosa = nr_seq_motivo_glosa_w
	and	ie_evento = 'CC';

	if (qt_registro_w = 0) then
	--	Tasy_posicionar_sequence('PLS_GLOSA_EVENTO','NR_SEQUENCIA','N');
		CALL pls_inserir_evento_glosa(nr_seq_motivo_glosa_w,'CC','dgkorz');
	end if;

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_glosa_evento
	where	nr_seq_motivo_glosa = nr_seq_motivo_glosa_w
	and	ie_evento = 'DC';

	if (qt_registro_w = 0) then
		--Tasy_posicionar_sequence('PLS_GLOSA_EVENTO','NR_SEQUENCIA','N');
		begin
		CALL pls_inserir_evento_glosa(nr_seq_motivo_glosa_w,'DC','dgkorz');
		end;
	end if;
end if;


