CREATE OR REPLACE PROCEDURE baca_pls_ajustar_forma_cobr () AS $body$
DECLARE


nr_seq_mensalidade_w	bigint;
nr_seq_pagador_w	bigint;
nr_seq_lote_w		bigint;
nr_seq_forma_cobranca_w	bigint;
nr_seq_conta_banco_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_pagador,
		nr_seq_lote
	from	pls_mensalidade
	where	ie_cancelamento	= 'E';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_mensalidade_w,
	nr_seq_pagador_w,
	nr_seq_lote_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	begin
	select	nr_seq_forma_cobranca,
		nr_seq_conta_banco
	into STRICT	nr_seq_forma_cobranca_w,
		nr_seq_conta_banco_w
	from	pls_mensalidade
	where	nr_seq_pagador	= nr_seq_pagador_w
	and	nr_seq_lote	= nr_seq_lote_w
	and	ie_cancelamento	= 'C';
	exception
	when others then
		nr_seq_forma_cobranca_w	:= null;
		nr_seq_conta_banco_w	:= null;
	end;

	update	pls_mensalidade
	set	nr_seq_forma_cobranca	= nr_seq_forma_cobranca_w,
		nr_seq_conta_banco	= nr_seq_conta_banco_w
	where	nr_sequencia		= nr_seq_mensalidade_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
