CREATE OR REPLACE PROCEDURE baca_pls_ajustar_glosa_9918 () AS $body$
DECLARE


nr_seq_glosa_w		bigint;
qt_glosa_9918_w		bigint;
nr_seq_glosa_9918_w	bigint;
nr_seq_glosa_evento_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	tiss_motivo_glosa 	a
	where	cd_motivo_tiss		=  9918;

BEGIN

select	count(a.nr_sequencia)
into STRICT	qt_glosa_9918_w
from	tiss_motivo_glosa 	a
where	cd_motivo_tiss		=  9918;

open C01;
loop
fetch C01 into
	nr_seq_glosa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if	((qt_glosa_9918_w-c01%ROWCOUNT) > 0) then

		begin
			delete	FROM pls_glosa_evento
			where	nr_seq_motivo_glosa = nr_seq_glosa_w;

			delete	FROM tiss_motivo_glosa
			where	nr_sequencia	= nr_seq_glosa_w;
		exception
		when others then
			null;
		end;

	end if;

	end;
end loop;
close C01;

select	max(a.nr_sequencia)
into STRICT	nr_seq_glosa_9918_w
from	tiss_motivo_glosa 	a
where	cd_motivo_tiss		=  9918;

if (coalesce(nr_seq_glosa_9918_w,0) = 0) then

	select	nextval('tiss_motivo_glosa_seq')
	into STRICT	nr_seq_glosa_9918_w
	;

	insert into tiss_motivo_glosa(nr_sequencia, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec, ie_grupo_glosa,
			cd_motivo_tiss, ds_motivo_tiss, ie_acao_pls,
			cd_convenio, cd_motivo_glosa, ie_fechar_conta,
			ie_importacao_ops)
		values (nr_seq_glosa_9918_w, clock_timestamp(), 'Tasy',
			clock_timestamp(), 'Tasy', '21',
			9918, 'Profissional executor n√£o cadastrado', 'S',
			null, null, 'S',
			'S');

end if;

select	max(nr_sequencia)
into STRICT	nr_seq_glosa_evento_w
from	pls_glosa_evento
where	nr_seq_motivo_glosa = nr_seq_glosa_9918_w;

if (coalesce(nr_seq_glosa_evento_w,0) = 0) then

	select	nextval('pls_glosa_evento_seq')
	into STRICT	nr_seq_glosa_evento_w
	;

	insert into pls_glosa_evento(	ie_evento, ie_plano, nr_sequencia,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			nm_usuario_nrec, nr_seq_motivo_glosa)
	values (	'CC', 'N', nr_seq_glosa_evento_w,
			clock_timestamp(), 'Tasy', clock_timestamp(),
			'Tasy', nr_seq_glosa_9918_w);

end if;

end;
