CREATE OR REPLACE PROCEDURE baca_pls_ajustar_lanc_automat ( cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_regra_w			bigint;
cd_estabelecimento_w		bigint;
dt_atualizacao_w		timestamp;
nm_usuario_w			varchar(255);
dt_atualizacao_nrec_w		timestamp;
nm_usuario_nrec_w		varchar(255);
ie_situacao_w			varchar(255);
ie_evento_w			varchar(255);
ie_tipo_guia_w			varchar(255);
nr_seq_prestador_w		bigint;
ie_consulta_urgencia_w		varchar(255);
dt_atualizacao_ww		timestamp;
nm_usuario_ww			varchar(255);
dt_atualizacao_nrec_ww		timestamp;
nm_usuario_nrec_ww		varchar(255);
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_regra_nova_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_situacao,
		ie_evento,
		ie_tipo_guia,
		nr_seq_prestador,
		ie_consulta_urgencia
	from	pls_regra_lanc;

C02 CURSOR FOR
	SELECT	dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_procedimento,
		ie_origem_proced
	from	pls_regra_lanc_item
	where	nr_seq_regra	= nr_seq_regra_w;

C03 CURSOR FOR
	SELECT	nr_sequencia,
		ie_tipo_guia,
		cd_procedimento,
		ie_origem_proced,
		ie_consulta_urgencia,
		ie_situacao,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec
	from	pls_regra_proced_guia;

BEGIN



open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	cd_estabelecimento_w,
	dt_atualizacao_w,
	nm_usuario_w,
	dt_atualizacao_nrec_w,
	nm_usuario_nrec_w,
	ie_situacao_w,
	ie_evento_w,
	ie_tipo_guia_w,
	nr_seq_prestador_w,
	ie_consulta_urgencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('pls_regra_lanc_automatico_seq')
	into STRICT	nr_seq_regra_nova_w
	;

	insert	into pls_regra_lanc_automatico(nr_sequencia, ie_situacao, cd_estabelecimento,
		 ds_regra, ie_evento, dt_atualizacao,
		 nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		 ie_tipo_guia, nr_seq_prestador, ie_consulta_urgencia)
	values (nr_seq_regra_nova_w, ie_situacao_w, cd_estabelecimento_w,
		 substr('Regra Lanç. de Proced na Autorização seq '||nr_seq_regra_w,1,255), CASE WHEN ie_evento_w='AU' THEN 8  ELSE 9 END , dt_atualizacao_w,
		 nm_usuario_w, dt_atualizacao_nrec_w, nm_usuario_nrec_w,
		 ie_tipo_guia_w, nr_seq_prestador_w, ie_consulta_urgencia_w);
	open C02;
	loop
	fetch C02 into
		dt_atualizacao_ww,
		nm_usuario_ww,
		dt_atualizacao_nrec_ww,
		nm_usuario_nrec_ww,
		cd_procedimento_w,
		ie_origem_proced_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		insert	into pls_regra_lanc_aut_item(nr_sequencia, nr_seq_regra, ie_situacao,
			 dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			 nm_usuario_nrec, cd_procedimento, ie_origem_proced,
			 ie_tipo_regra)
		values (nextval('pls_regra_lanc_aut_item_seq'), nr_seq_regra_nova_w, 'A',
			 dt_atualizacao_ww, nm_usuario_ww, dt_atualizacao_nrec_ww,
			 nm_usuario_nrec_ww, cd_procedimento_w, ie_origem_proced_w,
			 'G');
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

open C03;
loop
fetch C03 into
	nr_seq_regra_w,
	ie_tipo_guia_w,
	cd_procedimento_w,
	ie_origem_proced_w,
	ie_consulta_urgencia_w,
	ie_situacao_w,
	dt_atualizacao_w,
	nm_usuario_w,
	dt_atualizacao_nrec_w,
	nm_usuario_nrec_w;
EXIT WHEN NOT FOUND; /* apply on C03 */
	begin
	select	nextval('pls_regra_lanc_automatico_seq')
	into STRICT	nr_seq_regra_nova_w
	;

	insert	into pls_regra_lanc_automatico(nr_sequencia, ie_situacao, cd_estabelecimento,
		 ds_regra, ie_evento, dt_atualizacao,
		 nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		 ie_tipo_guia, ie_consulta_urgencia)
	values (nr_seq_regra_nova_w, ie_situacao_w, cd_estabelecimento_p,
		 substr('Guia X Procedimento seq '||nr_seq_regra_w,1,255), 9, dt_atualizacao_w,
		 nm_usuario_w, dt_atualizacao_nrec_w, nm_usuario_nrec_w,
		 ie_tipo_guia_w, ie_consulta_urgencia_w);

	if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') and (ie_origem_proced_w IS NOT NULL AND ie_origem_proced_w::text <> '') then
		insert	into pls_regra_lanc_aut_item(nr_sequencia, nr_seq_regra, ie_situacao,
			 dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			 nm_usuario_nrec, cd_procedimento, ie_origem_proced,
			 ie_tipo_regra)
		values (nextval('pls_regra_lanc_aut_item_seq'), nr_seq_regra_nova_w, 'A',
			 dt_atualizacao_w, nm_usuario_w, dt_atualizacao_nrec_w,
			 nm_usuario_nrec_w, cd_procedimento_w, ie_origem_proced_w,
			 'G');
	end if;
	end;
end loop;
close C03;

commit;

end;
$body$
