CREATE OR REPLACE PROCEDURE baca_pls_ajusta_reg_compl_guia () AS $body$
DECLARE


nr_seq_regra_w	bigint;
ie_guia_gat_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_guia_gat
	from	pls_regra_conta_compl;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	ie_guia_gat_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ie_guia_gat_w,'N')	= 'S') then
		update	pls_regra_conta_compl
		set	ie_aplicacao_guia	= 'G'
		where	nr_sequencia		= nr_seq_regra_w;
	elsif (coalesce(ie_guia_gat_w,'N')	= 'N') then
		update	pls_regra_conta_compl
		set	ie_aplicacao_guia	= 'N'
		where	nr_sequencia		= nr_seq_regra_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
