SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_ajuste_lote ( nm_usuario_p text ) AS $body$
DECLARE

 
nr_seq_prestador_w		bigint;
nr_seq_conta_lote_w		bigint;

nr_seq_prestador_imp_w		varchar(25);
cd_cgc_prestador_imp_w		varchar(25);
nr_cpf_prestador_imp_w		varchar(25);

nr_seq_protocolo_w		bigint;
cd_estabelecimento_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_seq_prestador_imp, 
		cd_cgc_prestador_imp, 
		nr_cpf_prestador_imp, 
		cd_estabelecimento 
	from	pls_protocolo_Conta 
	where	ie_forma_imp = 'P' 
	and	coalesce(nr_seq_lote_conta,0) = 0 
	and	ie_situacao in ('RE','A','I')	 
	group by nr_seq_prestador_imp, 
		 cd_cgc_prestador_imp, 
		 nr_cpf_prestador_imp, 
		 cd_estabelecimento;

C02 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_protocolo_conta 
	where	ie_forma_imp = 'P' 
	and	coalesce(nr_seq_lote_conta,0) = 0 
	and	ie_situacao in ('RE','A','I') 
	and (nr_seq_prestador_imp = nr_seq_prestador_imp_w 
	or	cd_cgc_prestador_imp = cd_cgc_prestador_imp_w 
	or	nr_cpf_prestador_imp = nr_cpf_prestador_imp_w);


BEGIN 
 
open C01;
loop 
fetch C01 into 
	nr_seq_prestador_imp_w, 
	cd_cgc_prestador_imp_w, 
	nr_cpf_prestador_imp_w, 
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	nr_seq_prestador_w := 	pls_obter_prestador_imp(cd_cgc_prestador_imp_w, nr_cpf_prestador_imp_w, nr_seq_prestador_imp_w, 
							null, null, null);
 
	nr_seq_conta_lote_w := pls_gerar_lote_analise( nr_seq_prestador_w, null, nm_usuario_p, cd_estabelecimento_w, null, nr_seq_conta_lote_w);
 
	open C02;
	loop 
	fetch C02 into 
		nr_seq_protocolo_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
 
		update	pls_protocolo_conta 
		set	nr_seq_lote_conta	= nr_seq_conta_lote_w, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_sequencia		= nr_seq_protocolo_w;
 
		end;
	end loop;
	close C02;
 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
