SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_ajuste_parametro_web () AS $body$
DECLARE



/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Procedure criada para acertar os parâmetros da função OPSW - Guia Médico
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  x ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_registro_w			integer := 0;
ie_categoria_guia_medico_w      varchar(2);
ie_carecter_esp_guia_medico_w   varchar(1);
ie_guia_sem_rodape_w            varchar(1);
ie_produto_guia_medico_w        varchar(5);
ie_endereco_espec_w		varchar(1);


BEGIN

select 	count(1)
into STRICT	qt_registro_w
from	pls_web_param_geral;

if ( qt_registro_w > 0 ) then

	select 	count(1)
	into STRICT	qt_registro_w
	from	tabela_sistema
	where	upper(nm_tabela) = 'PLS_WEB_PARAM_GUIA_MEDICO';


	if ( qt_registro_w > 0 ) then

		select 	ie_categoria_guia_medico,
			ie_carecter_esp_guia_medico,
			ie_guia_sem_rodape,
			ie_produto_guia_medico,
			ie_endereco_espec
		into STRICT	ie_categoria_guia_medico_w,
			ie_carecter_esp_guia_medico_w,
			ie_guia_sem_rodape_w,
			ie_produto_guia_medico_w,
			ie_endereco_espec_w
		from	pls_web_param_geral LIMIT 1;

		select 	count(1)
		into STRICT	qt_registro_w
		from	pls_web_param_guia_medico;


		if ( qt_registro_w > 0 ) then
			update 	pls_web_param_guia_medico
			set	ie_categoria_guia_medico 	= ie_categoria_guia_medico_w,
				ie_carecter_esp_guia_medico 	= ie_carecter_esp_guia_medico_w,
				ie_guia_sem_rodape 		= ie_guia_sem_rodape_w,
				ie_produto_guia_medico 		= ie_produto_guia_medico_w,
				ie_endereco_espec 		= ie_endereco_espec_w;

		else
			insert into pls_web_param_guia_medico(
					nr_sequencia, dt_atualizacao, nm_usuario,
					dt_atualizacao_nrec, nm_usuario_nrec, ie_categoria_guia_medico,
					ie_carecter_esp_guia_medico, ie_novo_guia_medico, ie_guia_sem_rodape,
					ie_produto_guia_medico, ie_endereco_espec, ie_exibe_bairro_prestador,
					ie_tipo_prestador_consulta)
				values (	nextval('pls_web_param_guia_medico_seq'), clock_timestamp(), 'BACA',
					clock_timestamp(), 'BACA', ie_categoria_guia_medico_w,
					ie_carecter_esp_guia_medico_w, null, ie_guia_sem_rodape_w,
					ie_produto_guia_medico_w, ie_endereco_espec_w, 'S',
					'S');
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_pls_ajuste_parametro_web () FROM PUBLIC;

