SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_pls_ajust_perm_exclu_item () AS $body$
DECLARE


vl_parametro_w			varchar(2);
nm_usuario_param_w		varchar(255);
nr_seq_membro_w			bigint;

C01 CURSOR FOR
	SELECT	vl_parametro,
		nm_usuario_param
	from	funcao_param_usuario
	where	cd_funcao	= 1270
	and	nr_sequencia	= 14;

C02 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_membro_grupo_aud	a,
		pls_grupo_auditor	b
	where	a.nr_seq_grupo		= b.nr_sequencia
	and	a.nm_usuario_exec	= nm_usuario_param_w;


BEGIN

open C01;
loop
fetch C01 into
	vl_parametro_w,
	nm_usuario_param_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_membro_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		update	pls_membro_grupo_aud
		set	ie_excluir_item	= vl_parametro_w
		where	nr_sequencia	= nr_seq_membro_w;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
