CREATE OR REPLACE PROCEDURE baca_pls_atualizar_ordem_int () AS $body$
DECLARE


nr_seq_item_resumo_w	bigint;
nr_ordem_w		bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia,
		CASE WHEN b.ie_tipo_item || b.ie_tipo_despesa='P1' THEN '1' WHEN b.ie_tipo_item || b.ie_tipo_despesa='P2' THEN '3' WHEN b.ie_tipo_item || b.ie_tipo_despesa='P3' THEN '2' WHEN b.ie_tipo_item || b.ie_tipo_despesa='P4' THEN '4' WHEN b.ie_tipo_item || b.ie_tipo_despesa='M1' THEN '6' WHEN b.ie_tipo_item || b.ie_tipo_despesa='M2' THEN '5' WHEN b.ie_tipo_item || b.ie_tipo_despesa='M3' THEN '7' WHEN b.ie_tipo_item || b.ie_tipo_despesa='M7' THEN '8' WHEN b.ie_tipo_item || b.ie_tipo_despesa='R1' THEN '1' WHEN b.ie_tipo_item || b.ie_tipo_despesa='R2' THEN '3' WHEN b.ie_tipo_item || b.ie_tipo_despesa='R3' THEN '2' WHEN b.ie_tipo_item || b.ie_tipo_despesa='R4' THEN '4' END
	from	pls_analise_conta a,
		w_pls_resumo_conta b
	where	a.nr_sequencia 		= b.nr_seq_analise
	and	a.ie_origem_analise	= 3;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_item_resumo_w,
	nr_ordem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	w_pls_resumo_conta
	set	nr_ordem	= nr_ordem_w
	where	nr_sequencia	= nr_seq_item_resumo_w;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
